;; **
;;; # Heartily
;;; 
;;; ### This is a notebook-style analysis of one-month's worth of workout data
;; **

;; @@
(ns stunning-hurricane
  (:require [gorilla-plot.core :as plot]))
(use 'heartily.core)
(init)
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; **
;;; ----
;;; ### Let's try loading heartbeats for a single workout
;; **

;; @@
(def hr1 (heart-beats-for "trek1.gpx"))
(plot/list-plot hr1 :joined true :color "green")

